import React, { useState, useEffect } from "react";
import "./Home.css";

const cityCoordinates = {
  "Tbilisi": { latitude: 41.7151, longitude: 44.8271 },
  "Batumi": { latitude: 41.6168, longitude: 41.6367 },
  "Kutaisi": { latitude: 42.2500, longitude: 42.7000 },
};

const weatherIcons = {
  0: "‚òÄÔ∏è", 1: "üå§Ô∏è", 2: "‚õÖ", 3: "‚òÅÔ∏è", 45: "üå´Ô∏è", 48: "üå´Ô∏è", // clear, mainly clear, partly cloudy, overcast, fog
  51: "üå¶Ô∏è", 53: "üå¶Ô∏è", 55: "üåßÔ∏è", // drizzle
  61: "üåßÔ∏è", 63: "üåßÔ∏è", 65: "üåßÔ∏è", // rain
  71: "üå®Ô∏è", 73: "üå®Ô∏è", 75: "üå®Ô∏è", // snow
  80: "üå¶Ô∏è", 81: "üå¶Ô∏è", 82: "üåßÔ∏è", // showers
};

const weatherDescriptions = {
  0: "·Éõ·Éñ·Éò·Éê·Éú·Éò", 1: "·É£·Éõ·Éî·É¢·Éî·É°·Éê·Éì ·Éõ·Éñ·Éò·Éê·Éú·Éò", 2: "·Éú·Éê·É¨·Éò·Éö·Éù·Éë·É†·Éò·Éï ·Éõ·Éù·É¶·É†·É£·Éë·Éö·É£·Éö·Éò", 3: "·É¶·É†·É£·Éë·Éö·Éò·Éê·Éú·Éò", 45: "·Éú·Éò·É°·Éö·Éò", 48: "·Éú·Éò·É°·Éö·Éò",
  51: "·Éõ·É°·É£·Éë·É£·É•·Éò ·Éü·Éù·Éú·Éï·Éê", 53: "·É°·Éê·É®·É£·Éê·Éö·Éù ·Éü·Éù·Éú·Éï·Éê", 55: "·Éõ·É´·Éö·Éê·Éï·É†·Éò ·Éü·Éù·Éú·Éï·Éê",
  61: "·Éõ·É°·É£·Éë·É£·É•·Éò ·É¨·Éï·Éò·Éõ·Éê", 63: "·É°·Éê·É®·É£·Éê·Éö·Éù ·É¨·Éï·Éò·Éõ·Éê", 65: "·Éõ·É´·Éö·Éê·Éï·É†·Éò ·É¨·Éï·Éò·Éõ·Éê",
  71: "·Éõ·É°·É£·Éë·É£·É•·Éò ·Éó·Éù·Éï·Éê", 73: "·É°·Éê·É®·É£·Éê·Éö·Éù ·Éó·Éù·Éï·Éê", 75: "·Éõ·É´·Éö·Éê·Éï·É†·Éò ·Éó·Éù·Éï·Éê",
  80: "·Éõ·É°·É£·Éë·É£·É•·Éò ·É¨·Éï·Éò·Éõ·Éê", 81: "·É°·Éê·É®·É£·Éê·Éö·Éù ·É¨·Éï·Éò·Éõ·Éê", 82: "·É´·Éö·Éò·Éî·É†·Éò ·É¨·Éï·Éò·Éõ·Éê",
};

function getWeatherIcon(code) {
  return weatherIcons[code] || "‚ùì";
}
function getWeatherDescription(code) {
  return weatherDescriptions[code] || "·É£·É™·Éú·Éù·Éë·Éò";
}
function formatDate(dateString) {
  const date = new Date(dateString);
  const days = ["·Éô·Éï·Éò·É†·Éê", "·Éù·É†·É®", "·É°·Éê·Éõ", "·Éù·Éó·ÉÆ", "·ÉÆ·É£·Éó", "·Éû·Éê·É†", "·É®·Éê·Éë"];
  const months = ["·Éò·Éê·Éú·Éï·Éê·É†·Éò","·Éó·Éî·Éë·Éî·É†·Éï·Éê·Éö·Éò","·Éõ·Éê·É†·É¢·Éò","·Éê·Éû·É†·Éò·Éö·Éò","·Éõ·Éê·Éò·É°·Éò","·Éò·Éï·Éú·Éò·É°·Éò","·Éò·Éï·Éö·Éò·É°·Éò","·Éê·Éí·Éï·Éò·É°·É¢·Éù","·É°·Éî·É•·É¢·Éî·Éõ·Éë·Éî·É†·Éò","·Éù·É•·É¢·Éù·Éõ·Éë·Éî·É†·Éò","·Éú·Éù·Éî·Éõ·Éë·Éî·É†·Éò","·Éì·Éî·Éô·Éî·Éõ·Éë·Éî·É†·Éò"];
  return `${date.getDate()} ${months[date.getMonth()]}, ${days[date.getDay()]}`;
}
function getTime() {
  const now = new Date();
  return now.toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'});
}

export default function Home() {
  const [selectedCity, setSelectedCity] = useState("Tbilisi");
  const [weather, setWeather] = useState(null);
  const [forecast, setForecast] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  useEffect(() => {
    async function fetchWeather(city) {
      setLoading(true);
      setError("");
      setWeather(null);
      setForecast([]);
      try {
        const coords = cityCoordinates[city];
        // Open-Meteo daily for 3 days, current weather
        const url = `https://api.open-meteo.com/v1/forecast?latitude=${coords.latitude}&longitude=${coords.longitude}&current_weather=true&daily=temperature_2m_max,temperature_2m_min,weathercode&timezone=auto`;
        const res = await fetch(url);
        if (!res.ok) throw new Error("Weather API error");
        const data = await res.json();
        setWeather(data.current_weather);

        // Build forecast for next 3 days (skip today)
        const forecastData = [];
        for (let i = 1; i <= 3; i++) {
          forecastData.push({
            date: data.daily.time[i],
            min: data.daily.temperature_2m_min[i],
            max: data.daily.temperature_2m_max[i],
            code: data.daily.weathercode[i],
          });
        }
        setForecast(forecastData);
      } catch (err) {
        setError("·Éê·Éõ·Éò·Éú·Éì·Éò·É° ·Éõ·Éù·Éú·Éê·É™·Éî·Éõ·Éî·Éë·Éò·É° ·Éõ·Éò·É¶·Éî·Éë·Éê ·Éï·Éî·É† ·Éõ·Éù·ÉÆ·Éî·É†·ÉÆ·Éì·Éê.");
      }
      setLoading(false);
    }
    fetchWeather(selectedCity);
  }, [selectedCity]);

  return (
    <main className="weather-h-main">
      <div className="weather-h-card">
        <div className="weather-h-left">
          <div className="weather-h-city-select">
            <h1 className="weather-h-city">{selectedCity}</h1>
            <select value={selectedCity} onChange={e => setSelectedCity(e.target.value)}>
              {Object.keys(cityCoordinates).map(city => (
                <option key={city} value={city}>{city}</option>
              ))}
            </select>
          </div>
          <div className="weather-h-date">{formatDate(new Date())}, {getTime()}</div>
          <div className="weather-h-current">
            <span className="weather-h-icon" style={{fontSize: 80}}>
              {weather ? getWeatherIcon(weather.weathercode) : "‚è≥"}
            </span>
            <span className="weather-h-temp">
              {weather && !loading ? `${Math.round(weather.temperature)}¬∞` : ""}
            </span>
          </div>
          <div className="weather-h-details">
            <div>·É•·Éê·É†·Éò: {weather ? weather.windspeed + " ·Éô·Éõ/·É°" : "-"}</div>
            <div>·Éê·Éõ·Éò·Éú·Éì·Éò: {weather ? getWeatherDescription(weather.weathercode) : "-"}</div>
          </div>
          {error && <div className="weather-h-error">{error}</div>}
        </div>
        <div className="weather-h-right">
          <div className="weather-h-forecast-title">·Éê·Éõ·Éò·Éú·Éì·Éò·É° ·Éû·É†·Éù·Éí·Éú·Éù·Éñ·Éò</div>
          <div className="weather-h-forecast-cards">
            {forecast.map((f, idx) => (
              <div className="weather-h-forecast-card" key={idx}>
                <div className="weather-h-forecast-date">{formatDate(f.date)}</div>
                <div className="weather-h-forecast-icon" style={{fontSize: 36}}>{getWeatherIcon(f.code)}</div>
                <div className="weather-h-forecast-desc">{getWeatherDescription(f.code)}</div>
                <div className="weather-h-forecast-temp">{Math.round(f.min)}¬∞ / {Math.round(f.max)}¬∞</div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </main>
  );
}
