import React from "react";
import useApiData from "../hooks/useApiData";

function Home() {
  const { data, loading, error } = useApiData("/posts?_limit=5");

  return (
    <main>
      <h1>Home Page</h1>
      <p>Latest Posts (from API):</p>
      {loading && <p>Loading...</p>}
      {error && <p style={{ color: "red" }}>{error}</p>}
      <ul>
        {data &&
          data.map((post) => (
            <li key={post.id}>
              <b>{post.title}</b>
              <p>{post.body}</p>
            </li>
          ))}
      </ul>
    </main>
  );
}

export default Home;
