import React, { useState } from "react";
import "./Profile.scss";

function Profile() {
  // Example: save default city in localStorage
  const [defaultCity, setDefaultCity] = useState(
    localStorage.getItem("defaultCity") || "Tbilisi"
  );

  const handleChange = (e) => {
    setDefaultCity(e.target.value);
    localStorage.setItem("defaultCity", e.target.value);
  };

  return (
    <div className="profile-container">
      <div className="profile-card">
        <img
          src={`https://ui-avatars.com/api/?name=User&background=90caf9&color=274472`}
          alt="avatar"
          className="profile-avatar"
        />
        <h2>Your Preferences</h2>
        <div>
          <label>
            Default City: {" "}
            <select value={defaultCity} onChange={handleChange}>
              <option>Tbilisi</option>
              <option>Batumi</option>
              <option>Kutaisi</option>
              <option>Rustavi</option>
              <option>Zugdidi</option>
              <option>Gori</option>
              <option>Poti</option>
              <option>Telavi</option>
              <option>Akhaltsikhe</option>
              <option>Samtredia</option>
            </select>
          </label>
        </div>
        <p style={{marginTop: "1.5em", color: "#7d8da1"}}>Your default city will be shown first on the Home page.</p>
      </div>
    </div>
  );
}

export default Profile;
