import React, { useState } from "react";

const cityList = [
  "Tbilisi", "Batumi", "Kutaisi", "Rustavi", "Zugdidi",
  "Gori", "Poti", "Telavi", "Akhaltsikhe", "Samtredia"
];

function Profile() {
  const [defaultCity, setDefaultCity] = useState(
    localStorage.getItem("defaultCity") || "Tbilisi"
  );

  const handleChange = (e) => {
    setDefaultCity(e.target.value);
    localStorage.setItem("defaultCity", e.target.value);
  };

  return (
    <div style={{ padding: "2rem" }}>
      <h2>Profile</h2>
      <label>
        Default City:
        <select value={defaultCity} onChange={handleChange} style={{ marginLeft: "1em" }}>
          {cityList.map(city => (
            <option key={city} value={city}>{city}</option>
          ))}
        </select>
      </label>
      <p style={{ color: "#888", marginTop: "1em" }}>
        Your selected default city will appear first on the Home page.
      </p>
    </div>
  );
}

export default Profile;
