import React, { useState, useEffect } from "react";
import "./Home.css";

// Coordinates for some Georgian cities
const cityCoordinates = {
  "Tbilisi": { latitude: 41.7151, longitude: 44.8271 },
  "Batumi": { latitude: 41.6168, longitude: 41.6367 },
  "Kutaisi": { latitude: 42.2500, longitude: 42.7000 },
};

const weatherIcons = {
  0: "‚òÄÔ∏è", // clear sky
  1: "üå§Ô∏è", // mainly clear
  2: "üå¶Ô∏è", // partly cloudy
  3: "‚òÅÔ∏è", // overcast
  45: "üå´Ô∏è", // fog
  48: "üå´Ô∏è", // depositing rime fog
  51: "üå¶Ô∏è", // drizzle: light
  53: "üå¶Ô∏è", // drizzle: moderate
  55: "üå¶Ô∏è", // drizzle: dense
  61: "üåßÔ∏è", // rain: slight
  63: "üåßÔ∏è", // rain: moderate
  65: "üåßÔ∏è", // rain: heavy
  71: "üå®Ô∏è", // snow fall: slight
  73: "üå®Ô∏è", // snow fall: moderate
  75: "üå®Ô∏è", // snow fall: heavy
  80: "üå¶Ô∏è", // rain showers: slight
  81: "üå¶Ô∏è", // rain showers: moderate
  82: "üå¶Ô∏è", // rain showers: violent
};

function getWeatherIcon(code) {
  return weatherIcons[code] || "‚ùì";
}

function getGeorgianDate() {
  const now = new Date();
  const days = ["·Éô·Éï·Éò·É†·Éê", "·Éù·É†·É®·Éê·Éë·Éê·Éó·Éò", "·É°·Éê·Éõ·É®·Éê·Éë·Éê·Éó·Éò", "·Éù·Éó·ÉÆ·É®·Éê·Éë·Éê·Éó·Éò", "·ÉÆ·É£·Éó·É®·Éê·Éë·Éê·Éó·Éò", "·Éû·Éê·É†·Éê·É°·Éô·Éî·Éï·Éò", "·É®·Éê·Éë·Éê·Éó·Éò"];
  const months = ["·Éò·Éê·Éú·Éï·Éê·É†·Éò","·Éó·Éî·Éë·Éî·É†·Éï·Éê·Éö·Éò","·Éõ·Éê·É†·É¢·Éò","·Éê·Éû·É†·Éò·Éö·Éò","·Éõ·Éê·Éò·É°·Éò","·Éò·Éï·Éú·Éò·É°·Éò","·Éò·Éï·Éö·Éò·É°·Éò","·Éê·Éí·Éï·Éò·É°·É¢·Éù","·É°·Éî·É•·É¢·Éî·Éõ·Éë·Éî·É†·Éò","·Éù·É•·É¢·Éù·Éõ·Éë·Éî·É†·Éò","·Éú·Éù·Éî·Éõ·Éë·Éî·É†·Éò","·Éì·Éî·Éô·Éî·Éõ·Éë·Éî·É†·Éò"];
  return `${now.getDate()} ${months[now.getMonth()]}, ${days[now.getDay()]}`;
}

function getTime() {
  const now = new Date();
  return now.toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'});
}

function Home() {
  const [selectedCity, setSelectedCity] = useState("Tbilisi");
  const [weather, setWeather] = useState(null);
  const [weatherLoading, setWeatherLoading] = useState(false);
  const [weatherError, setWeatherError] = useState("");

  useEffect(() => {
    const fetchWeather = async (city) => {
      setWeatherLoading(true);
      setWeatherError("");
      setWeather(null);
      try {
        const coords = cityCoordinates[city];
        const url = `https://api.open-meteo.com/v1/forecast?latitude=${coords.latitude}&longitude=${coords.longitude}&current_weather=true`;
        const res = await fetch(url);
        if (!res.ok) throw new Error("Weather API error");
        const data = await res.json();
        setWeather(data.current_weather);
      } catch (err) {
        setWeatherError("Failed to fetch weather data.");
      }
      setWeatherLoading(false);
    };
    fetchWeather(selectedCity);
  }, [selectedCity]);

  return (
    <main className="weather-main">
      <div className="weather-card">
        <div className="weather-header">
          <h1 className="weather-city">{selectedCity}</h1>
          <select
            className="city-select"
            value={selectedCity}
            onChange={e => setSelectedCity(e.target.value)}
          >
            {Object.keys(cityCoordinates).map(city => (
              <option key={city} value={city}>{city}</option>
            ))}
          </select>
          <div className="weather-date">{getGeorgianDate()}</div>
          <div className="weather-time">{getTime()}</div>
        </div>
        <div className="weather-current">
          <span className="weather-icon" style={{fontSize: 80}}>
            {weather ? getWeatherIcon(weather.weathercode) : "‚è≥"}
          </span>
          <span className="weather-temp">
            {weather && !weatherLoading ? `${Math.round(weather.temperature)}¬∞` : ""}
          </span>
        </div>
        {weatherError && <div className="weather-error">{weatherError}</div>}
        <div className="weather-details">
          <div>·É•·Éê·É†·Éò: {weather ? weather.windspeed + " ·Éô·Éõ/·É°" : "-"}</div>
          <div>·Éê·Éõ·Éò·Éú·Éì·Éò·É° ·Éô·Éù·Éì·Éò: {weather ? weather.weathercode : "-"}</div>
        </div>
      </div>
    </main>
  );
}

export default Home;
