  const months = ["·Éò·Éê·Éú·Éï·Éê·É†·Éò","·Éó·Éî·Éë·Éî·É†·Éï·Éê·Éö·Éò","·Éõ·Éê·É†·É¢·Éò","·Éê·Éû·É†·Éò·Éö·Éò","·Éõ·Éê·Éò·É°·Éò","·Éò·Éï·Éú·Éò·É°·Éò","·Éò·Éï·Éö·Éò·É°·Éò","·Éê·Éí·Éï·Éò·É°·É¢·Éù","·É°·Éî·É•·É¢·Éî·Éõ·Éë·Éî·É†·Éò","·Éù·É•·É¢·Éù·Éõ·Éë·Éî·É†·Éò","·Éú·Éù·Éî·Éõ·Éë·Éî·É†·Éò","·Éì·Éî·Éô·Éî·Éõ·Éë·Éî·É†·Éò"];
  return `${date.getDate()} ${months[date.getMonth()]}, ${days[date.getDay()]}`;
}
function getTime() {
  const now = new Date();
  return now.toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'});
}
function formatTime(str) {
  // "2025-07-01T15:28" -> "15:28"
  return str?.split("T")[1] || "-";
}

function WeatherCityCard({city, coords}) {
  const [weather, setWeather] = useState(null);
  const [details, setDetails] = useState({});
  const [forecast, setForecast] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  useEffect(() => {
    async function fetchWeather() {
      setLoading(true);
      setError("");
      setWeather(null);
      setDetails({});
      setForecast([]);
      try {
        const url = `https://api.open-meteo.com/v1/forecast?latitude=${coords.latitude}&longitude=${coords.longitude}` +
          `&current_weather=true&daily=temperature_2m_max,temperature_2m_min,weathercode,sunrise,sunset&hourly=relative_humidity_2m&timezone=auto`;
        const res = await fetch(url);
        if (!res.ok) throw new Error("Weather API error");
        const data = await res.json();
        setWeather(data.current_weather);

        // Humidity (get current hour by matching time)
        let humidity = "-";
        if (data.hourly && data.hourly.time && data.hourly.relative_humidity_2m) {
          const now = new Date();
          const idx = data.hourly.time.findIndex(t => t.slice(0,13) === now.toISOString().slice(0,13));
          humidity = idx !== -1 ? data.hourly.relative_humidity_2m[idx] : "-";
        }

        setDetails({
          humidity,
          sunrise: data.daily.sunrise[0],
          sunset: data.daily.sunset[0],
        });

        // 5-day forecast (including today)
        const forecastData = [];
        for (let i = 0; i < 5; i++) {
          forecastData.push({
            date: data.daily.time[i],
            min: data.daily.temperature_2m_min[i],
            max: data.daily.temperature_2m_max[i],
            code: data.daily.weathercode[i],
          });
        }
        setForecast(forecastData);
      } catch (err) {
        setError("·Éê·Éõ·Éò·Éú·Éì·Éò·É° ·Éõ·Éù·Éú·Éê·É™·Éî·Éõ·Éî·Éë·Éò·É° ·Éõ·Éò·É¶·Éî·Éë·Éê ·Éï·Éî·É† ·Éõ·Éù·ÉÆ·Éî·É†·ÉÆ·Éì·Éê.");
      }
      setLoading(false);
    }
    fetchWeather();
  }, [city, coords.latitude, coords.longitude]);

  return (
    <div className="weather-ge-card">
      <div className="weather-ge-current">
        <div className="weather-ge-city-select">
          <h1 className="weather-ge-city">{city}</h1>
        </div>
        <div className="weather-ge-date">{formatDate(new Date())}, {getTime()}</div>
        <div className="weather-ge-maininfo">
          <span className="weather-ge-icon" style={{fontSize: 80}}>
            {weather ? getWeatherIcon(weather.weathercode) : "‚è≥"}
          </span>
          <span className="weather-ge-temp">
            {weather && !loading ? `${Math.round(weather.temperature)}¬∞` : ""}
          </span>
        </div>
        <div className="weather-ge-details">
          <div>·É•·Éê·É†·Éò: {weather ? weather.windspeed + " ·Éô·Éõ/·É°" : "-"}</div>
          <div>·É¢·Éî·Éú·Éò·Éê·Éú·Éù·Éë·Éê: {details.humidity !== undefined ? details.humidity + "%" : "-"}</div>
          <div>·Éê·Éõ·Éò·Éú·Éì·Éò: {weather ? getWeatherDescription(weather.weathercode) : "-"}</div>
          <div>·Éõ·Éñ·Éò·É° ·Éê·Éõ·Éù·É°·Éï·Éö·Éê: {details.sunrise ? formatTime(details.sunrise) : "-"}</div>
          <div>·Éõ·Éñ·Éò·É° ·É©·Éê·É°·Éï·Éö·Éê: {details.sunset ? formatTime(details.sunset) : "-"}</div>
        </div>
        {error && <div className="weather-ge-error">{error}</div>}
      </div>
      <div className="weather-ge-forecast-block">
        <div className="weather-ge-forecast-title">·Éê·Éõ·Éò·Éú·Éì·Éò·É° ·Éû·É†·Éù·Éí·Éú·Éù·Éñ·Éò</div>
        <div className="weather-ge-forecast-cards">
          {forecast.map((f, idx) => (
            <div className="weather-ge-forecast-card" key={idx}>
              <div className="weather-ge-forecast-date">{formatDate(f.date)}</div>
              <div className="weather-ge-forecast-icon" style={{fontSize: 36}}>{getWeatherIcon(f.code)}</div>
              <div className="weather-ge-forecast-desc">{getWeatherDescription(f.code)}</div>
              <div className="weather-ge-forecast-temp">{Math.round(f.min)}¬∞ / {Math.round(f.max)}¬∞</div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

