import React, { useState, useEffect } from "react";
import "./Profile.scss";

const cityList = [
  "Tbilisi", "Batumi", "Kutaisi", "Rustavi", "Zugdidi",
  "Gori", "Poti", "Telavi", "Akhaltsikhe", "Samtredia"
];

function getInitials(name) {
  return name
    .split(" ")
    .filter(Boolean)
    .map(word => word[0]?.toUpperCase())
    .join("")
    .slice(0,2) || "U";
}

function Profile() {
  const [name, setName] = useState(localStorage.getItem("userName") || "");
  const [defaultCity, setDefaultCity] = useState(localStorage.getItem("defaultCity") || "Tbilisi");

  useEffect(() => {
    localStorage.setItem("userName", name);
  }, [name]);

  useEffect(() => {
    localStorage.setItem("defaultCity", defaultCity);
  }, [defaultCity]);

  return (
    <div className="profile-container">
      <div className="profile-card">
        <div className="profile-avatar">
          <span>{getInitials(name || "User")}</span>
        </div>
        <h2>Welcome{ name && `, ${name}` }!</h2>
        <div className="profile-section">
          <label>
            <span>Your name:</span>
            <input
              type="text"
              value={name}
              placeholder="Enter your name"
              onChange={e => setName(e.target.value)}
              maxLength={24}
            />
          </label>
        </div>
        <div className="profile-section">
          <label>
            <span>Default City:</span>
            <select value={defaultCity} onChange={e => setDefaultCity(e.target.value)}>
              {cityList.map(city => (
                <option key={city} value={city}>{city}</option>
              ))}
            </select>
          </label>
        </div>
        <p className="profile-note">
          Your preferences are saved in your browser.
        </p>
      </div>
    </div>
  );
}

export default Profile;
