import React, { useState } from "react";
import "./Profile.css"; // or "./Profile.scss" if using SCSS

const cityList = [
  "Tbilisi", "Batumi", "Kutaisi", "Rustavi", "Zugdidi",
  "Gori", "Poti", "Telavi", "Akhaltsikhe", "Samtredia"
];

function Profile() {
  const [defaultCity, setDefaultCity] = useState(
    localStorage.getItem("defaultCity") || "Tbilisi"
  );

  // (Optional) Avatar letter
  const getInitial = () => defaultCity[0].toUpperCase();

  const handleChange = (e) => {
    setDefaultCity(e.target.value);
    localStorage.setItem("defaultCity", e.target.value);
  };

  return (
    <div className="profile-container">
      <div className="profile-card">
        <div className="profile-avatar">{getInitial()}</div>
        <h2>Profile</h2>
        <div className="profile-section">
          <label>
            Default City:
            <select value={defaultCity} onChange={handleChange}>
              {cityList.map(city => (
                <option key={city} value={city}>{city}</option>
              ))}
            </select>
          </label>
        </div>
        <div className="profile-note">
          Your selected default city will appear first on the Home page.
        </div>
      </div>
    </div>
  );
}

export default Profile;
